const trigger=document.getElementById("toggle"),box=document.getElementById("menu");toggle.addEventListener("click",(function(){box.classList.toggle("active")}));const anchors=document.querySelectorAll('a[href*="#"]');for(let o of anchors)o.addEventListener("click",(function(e){e.preventDefault();const t=o.getAttribute("href");document.querySelector(""+t).scrollIntoView({behavior:"smooth",block:"start"})}));function scrollFunction(){document.body.scrollTop>400||document.documentElement.scrollTop>400?document.querySelector(".arrowUp").style.display="block":document.querySelector(".arrowUp").style.display="none"}document.querySelector(".arrowUp").onclick=function(){document.body.scrollTop=0,document.documentElement.scrollTop=0},window.onscroll=function(){scrollFunction()};